mapper.select:
select
id, tenant_id, role_id, key_value, key_type
from back_role_detail


        and id = #{id}


        and tenant_id = #{tenantId}


        and role_id = #{roleId}


        and key_value = #{keyValue}


        and key_type = #{keyType}

select count(1)
from back_role_detail


        and id = #{id}


        and tenant_id = #{tenantId}


        and role_id = #{roleId}


        and key_value = #{keyValue}


        and key_type = #{keyType}
