mapper.select:
select
id, tenant_id, role_id, role_name, role_remark, create_time, last_modified_time
from back_sys_roles


        and id = #{id}


        and tenant_id = #{tenantId}


        and role_id = #{roleId}


        and role_name = #{roleName}


        and role_remark = #{roleRemark}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}

select count(1)
from back_sys_roles


        and id = #{id}


        and tenant_id = #{tenantId}


        and role_id = #{roleId}


        and role_name = #{roleName}


        and role_remark = #{roleRemark}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}
