select

from


                                                    and tenant_id = #{tenantId}
                                            and comment_pid = #{commentPid}
                                            and comment_id = #{commentId}
                                            and comment_type = #{commentType}
                                            and comment_desc = #{commentDesc}
                                            and user_id = #{userId}
                                            and status = #{status}
                                                                                    and article_content = #{articleContent}

select count(1)
from


                                        and tenant_id = #{tenantId}
                                and comment_pid = #{commentPid}
                                and comment_id = #{commentId}
                                and comment_type = #{commentType}
                                and comment_desc = #{commentDesc}
                                and user_id = #{userId}
                                and status = #{status}
                                                        and article_content = #{articleContent}

mapper.update:
update
tenant_id = #{tenantId}, comment_pid = #{commentPid}, comment_id = #{commentId}, comment_type = #{commentType}, comment_desc = #{commentDesc}, user_id = #{userId}, status = #{status}, article_content = #{articleContent},
where id = #{id} and tenant_id = #{tenantId}

mapper.delete:
delete from

where id = #{id}
