mapper.select:
select
id, tenant_id, brand_id, brand_name, brand_logo, company_name, brand_uscc, connect_uname, connect_mobile_no,
connect_wechat_id, brand_desc, company_desc, brand_detail, company_detail, vaild_date, expired_date, brand_fee,
brand_margin, remark, status, create_time, last_modified_time, brand_common_desc
from crm_brand_info


        and id = #{id}


        and tenant_id = #{tenantId}


        and brand_id = #{brandId}


        and brand_name = #{brandName}


        and brand_logo = #{brandLogo}


        and company_name = #{companyName}


        and brand_uscc = #{brandUscc}


        and connect_uname = #{connectUname}


        and connect_mobile_no = #{connectMobileNo}


        and connect_wechat_id = #{connectWechatId}


        and brand_desc = #{brandDesc}


        and company_desc = #{companyDesc}


        and brand_detail = #{brandDetail}


        and company_detail = #{companyDetail}


        and vaild_date = #{vaildDate}


        and expired_date = #{expiredDate}


        and brand_fee = #{brandFee}


        and brand_margin = #{brandMargin}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}


        and brand_common_desc = #{brandCommonDesc}

select count(1)
from crm_brand_info


        and id = #{id}


        and tenant_id = #{tenantId}


        and brand_id = #{brandId}


        and brand_name = #{brandName}


        and brand_logo = #{brandLogo}


        and company_name = #{companyName}


        and brand_uscc = #{brandUscc}


        and connect_uname = #{connectUname}


        and connect_mobile_no = #{connectMobileNo}


        and connect_wechat_id = #{connectWechatId}


        and brand_desc = #{brandDesc}


        and company_desc = #{companyDesc}


        and brand_detail = #{brandDetail}


        and company_detail = #{companyDetail}


        and vaild_date = #{vaildDate}


        and expired_date = #{expiredDate}


        and brand_fee = #{brandFee}


        and brand_margin = #{brandMargin}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}


        and brand_common_desc = #{brandCommonDesc}
