mapper.select:
select
id, tenant_id, prod_id, prod_group_id, prod_group_name, remark, status, create_time, last_modified_time
from crm_prod_group


        and id = #{id}


        and tenant_id = #{tenantId}


        and prod_id = #{prodId}


        and prod_group_id = #{prodGroupId}


        and prod_group_name = #{prodGroupName}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}

select count(1)
from crm_prod_group


        and id = #{id}


        and tenant_id = #{tenantId}


        and prod_id = #{prodId}


        and prod_group_id = #{prodGroupId}


        and prod_group_name = #{prodGroupName}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}
