mapper.select:
select
id, tenant_id, batch_id, brand_id, amount, arrival_time, order_time, spend, postage, trace_code, other_cost,
remark, status, create_time, last_modified_time
from crm_purches_record


        and id = #{id}


        and tenant_id = #{tenantId}


        and batch_id = #{batchId}


        and brand_id = #{brandId}


        and amount = #{amount}


        and arrival_time = #{arrivalTime}


        and order_time = #{orderTime}


        and spend = #{spend}


        and postage = #{postage}


        and trace_code = #{traceCode}


        and other_cost = #{otherCost}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}

select count(1)
from crm_purches_record


        and id = #{id}


        and tenant_id = #{tenantId}


        and batch_id = #{batchId}


        and brand_id = #{brandId}


        and amount = #{amount}


        and arrival_time = #{arrivalTime}


        and order_time = #{orderTime}


        and spend = #{spend}


        and postage = #{postage}


        and trace_code = #{traceCode}


        and other_cost = #{otherCost}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}
