mapper.select:
select
id, tenant_id, batch_id, amount, real_price, prod_id, prod_price, remark, status, create_time,
last_modified_time
from crm_sale_detail


        and id = #{id}


        and tenant_id = #{tenantId}


        and batch_id = #{batchId}


        and amount = #{amount}


        and real_price = #{realPrice}


        and prod_id = #{prodId}


        and prod_price = #{prodPrice}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}

select count(1)
from crm_sale_detail


        and id = #{id}


        and tenant_id = #{tenantId}


        and batch_id = #{batchId}


        and amount = #{amount}


        and real_price = #{realPrice}


        and prod_id = #{prodId}


        and prod_price = #{prodPrice}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}
