mapper.select:
select
id, tenant_id, batch_id, amount, order_time, spend, postage, postage_cost, trace_code, post_date, real_price,
remark, status, create_time, last_modified_time
from crm_sale_record


        and id = #{id}


        and tenant_id = #{tenantId}


        and batch_id = #{batchId}


        and amount = #{amount}


        and order_time = #{orderTime}


        and spend = #{spend}


        and postage = #{postage}


        and postage_cost = #{postageCost}


        and trace_code = #{traceCode}


        and post_date = #{postDate}


        and real_price = #{realPrice}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}

select count(1)
from crm_sale_record


        and id = #{id}


        and tenant_id = #{tenantId}


        and batch_id = #{batchId}


        and amount = #{amount}


        and order_time = #{orderTime}


        and spend = #{spend}


        and postage = #{postage}


        and postage_cost = #{postageCost}


        and trace_code = #{traceCode}


        and post_date = #{postDate}


        and real_price = #{realPrice}


        and remark = #{remark}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}
