mapper.select:
select
id, tenant_id, coupon_id, status, create_time, last_modified_time, rule_id
from market_coupon_rule


        and id = #{id}


        and tenant_id = #{tenantId}


        and coupon_id = #{couponId}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}


        and rule_id = #{ruleId}

select count(1)
from market_coupon_rule


        and id = #{id}


        and tenant_id = #{tenantId}


        and coupon_id = #{couponId}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}


        and rule_id = #{ruleId}
