mapper.select:
select
id, tenant_id, user_id, user_coupon_id, coupon_id, coupon_type, coupon_method, coupon_amount, use_vaild_date,
use_expire_date, status, create_time, last_modified_time
from market_user_coupon_info


        and id = #{id}


        and tenant_id = #{tenantId}


        and user_id = #{userId}


        and user_coupon_id = #{userCouponId}


        and coupon_id = #{couponId}


        and coupon_type = #{couponType}


        and coupon_method = #{couponMethod}


        and coupon_amount = #{couponAmount}


        and use_vaild_date = #{useVaildDate}


        and use_expire_date = #{useExpireDate}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}

select count(1)
from market_user_coupon_info


        and id = #{id}


        and tenant_id = #{tenantId}


        and user_id = #{userId}


        and user_coupon_id = #{userCouponId}


        and coupon_id = #{couponId}


        and coupon_type = #{couponType}


        and coupon_method = #{couponMethod}


        and coupon_amount = #{couponAmount}


        and use_vaild_date = #{useVaildDate}


        and use_expire_date = #{useExpireDate}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}
