mapper.select:
select
  id, tenant_id, flow_id, item_id, item_sort, item_type, status, last_modified_time, create_time
from sys_flow_items


        and id = #{id}


        and tenant_id = #{tenantId}


        and flow_id = #{flowId}


        and item_id = #{itemId}


        and item_sort = #{itemSort}


        and item_type = #{itemType}


        and status = #{status}


        and last_modified_time = #{lastModifiedTime}


        and create_time = #{createTime}

select count(1)
from sys_flow_items


        and id = #{id}


        and tenant_id = #{tenantId}


        and flow_id = #{flowId}


        and item_id = #{itemId}


        and item_sort = #{itemSort}


        and item_type = #{itemType}


        and status = #{status}


        and last_modified_time = #{lastModifiedTime}


        and create_time = #{createTime}
