mapper.select:
select

from


                                                and tenant_id = #{tenantId}
                                        and item_id = #{itemId}
                                        and item_sort = #{itemSort}
                                        and status = #{status}
                                                                                and item_title = #{itemTitle}
                                        and item_value = #{itemValue}
                                        and item_desc = #{itemDesc}
                                        and item_type = #{itemType}
                                        and title_show_type = #{titleShowType}
                                        and title_url = #{titleUrl}
                                        and value_url = #{valueUrl}
                                        and value_show_type = #{valueShowType}

select

from


                                                    and tenant_id = #{tenantId}
                                            and item_id = #{itemId}
                                            and item_sort = #{itemSort}
                                            and status = #{status}
                                                                                    and item_title = #{itemTitle}
                                            and item_value = #{itemValue}
                                            and item_desc = #{itemDesc}
                                            and item_type = #{itemType}
                                            and title_show_type = #{titleShowType}
                                            and title_url = #{titleUrl}
                                            and value_url = #{valueUrl}
                                            and value_show_type = #{valueShowType}

select count(1)
from


                                        and tenant_id = #{tenantId}
                                and item_id = #{itemId}
                                and item_sort = #{itemSort}
                                and status = #{status}
                                                        and item_title = #{itemTitle}
                                and item_value = #{itemValue}
                                and item_desc = #{itemDesc}
                                and item_type = #{itemType}
                                and title_show_type = #{titleShowType}
                                and title_url = #{titleUrl}
                                and value_url = #{valueUrl}
                                and value_show_type = #{valueShowType}

mapper.update:
update
tenant_id = #{tenantId}, item_id = #{itemId}, item_sort = #{itemSort}, status = #{status}, item_title = #{itemTitle}, item_value = #{itemValue}, item_desc = #{itemDesc}, item_type = #{itemType}, title_show_type = #{titleShowType}, title_url = #{titleUrl}, value_url = #{valueUrl}, value_show_type = #{valueShowType},
where id = #{id} and tenant_id = #{tenantId}

mapper.delete:
delete from

where id = #{id}
and tenant_id = #{tenantId}
