mapper.select:
select
  id, tenant_id, item_id, item_sort, status, create_time, last_modified_time, item_title, item_value, item_desc, title_show_type, value_show_type, title_url, value_url
from sys_item_info


        and id = #{id}


        and tenant_id = #{tenantId}


        and item_id = #{itemId}


        and item_sort = #{itemSort}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}


        and item_title = #{itemTitle}


        and item_value = #{itemValue}


        and item_desc = #{itemDesc}


        and title_show_type = #{titleShowType}


        and value_show_type = #{valueShowType}


        and title_url = #{titleUrl}


        and value_url = #{valueUrl}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_item_info


        and id = #{id}


        and tenant_id = #{tenantId}


        and item_id = #{itemId}


        and item_sort = #{itemSort}


        and status = #{status}


        and create_time = #{createTime}


        and last_modified_time = #{lastModifiedTime}


        and item_title = #{itemTitle}


        and item_value = #{itemValue}


        and item_desc = #{itemDesc}


        and title_show_type = #{titleShowType}


        and value_show_type = #{valueShowType}


        and title_url = #{titleUrl}


        and value_url = #{valueUrl}
