mapper.select:
select
  id, tenant_id, tenant_name, tenant_desc, register_time, mobile_no, vip_level, vip_expire_day, tenant_card_no, status, remark, createtime, lastmodifiedtime, tenant_favicon, tenant_logo, email
from sys_tenant_info


        and id = #{id}


        and tenant_id = #{tenantId}


        and tenant_name = #{tenantName}


        and tenant_desc = #{tenantDesc}


        and register_time = #{registerTime}


        and mobile_no = #{mobileNo}


        and vip_level = #{vipLevel}


        and vip_expire_day = #{vipExpireDay}


        and tenant_card_no = #{tenantCardNo}


        and status = #{status}


        and remark = #{remark}


        and createtime = #{createtime}


        and lastmodifiedtime = #{lastmodifiedtime}


        and tenant_favicon = #{tenantFavicon}


        and tenant_logo = #{tenantLogo}


        and email = #{email}

select count(1)
from sys_tenant_info


        and id = #{id}


        and tenant_id = #{tenantId}


        and tenant_name = #{tenantName}


        and tenant_desc = #{tenantDesc}


        and register_time = #{registerTime}


        and mobile_no = #{mobileNo}


        and vip_level = #{vipLevel}


        and vip_expire_day = #{vipExpireDay}


        and tenant_card_no = #{tenantCardNo}


        and status = #{status}


        and remark = #{remark}


        and createtime = #{createtime}


        and lastmodifiedtime = #{lastmodifiedtime}


        and tenant_favicon = #{tenantFavicon}


        and tenant_logo = #{tenantLogo}


        and email = #{email}
